// variant of https://shadertoy.com/view/fltBzn

void mainImage( out vec4 O, vec2 u )
{
    O = T(u);
}

#define H(p)  fract(sin(mod((p)* vec2(12.9898, 78.233),6.283)) * 43758.5453)
#define S(v)   smoothstep( 1.5/R.y, 0., clamp(v,0.,1.) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u / R.y, P;
    O =  .9998* T(u);                                  // dimmer with age
    for( float l, i=0.; i++ < 50.; ) {
        P = H(i-iTime); 
        P = .12*(.6+.5*cos(.1*iTime)) * sqrt(-2.*log(P.x)) * cos(6.283*P.y+vec2(0,1.57)); // Gaussian https://en.wikipedia.org/wiki/Normal_distribution#Generating_values_from_normal_distribution
        P += H(i)*R/R.y;
        l = length(U-P);
        O = mix( O*(1.-.2*S((l-.01)/6.)), vec4(.5+.5*S(l-.013)), S( l-.015 ) );
                 // ambient occlusion        circle pattern        mask
    }
}
