#define keyToggle(a)  ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)
#define S(v)            smoothstep(1., 0., abs(v)/fwidth(v) )

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u/R;

    O = texture(iChannel0, U);

        // --- color profile along horizontal axis
    vec4 V = texture(iChannel0, vec2(U.x,.5) ); V.a = .5; // video central line + ref .5

    float x = abs(2.*U.x-1.);  
    if keyToggle(32) V *= 1. + vec4(.07,.03,0,0) + vec4(.6,.2,.2,0)*x*x; // correction for my Logi720p

    V = S( U.y - V ); // draw RGB profile for mid-line.
    O = mix( O, vec4(1,0,0,1), V.r );
    O = mix( O, vec4(0,1,0,1), V.g );
    O = mix( O, vec4(0,0,1,1), V.b );
    O = mix( O, vec4(1), V.a );

        // --- color profile along vertical axis
    V = texture(iChannel0, vec2(.5,U.y) ); V.a = .5; // video central column + ref .5
    float y = abs(2.*U.y-1.);  
    if keyToggle(32) V *= 1. + vec4(.07,.03,0,0) + vec4(.18,.18,.18,0)*y*y; // correction for my Logi720p

    V = S( U.x - V ); // draw RGB profile for mid-line.
    O = mix( O, vec4(1,0,0,1), V.r );
    O = mix( O, vec4(0,1,0,1), V.g );
    O = mix( O, vec4(0,0,1,1), V.b );
    O = mix( O, vec4(1), V.a );

 // O *= 1. + vec4(.07,.03,0,0) + vec4(.6,.2,.2,0)*x*x + vec4(.18,.18,.18,0)*y*y;

        // --- white corner for qualibration.
    if (max(U.x,U.y)<.15) O = vec4(1);
}
