vec3 lgrnd( vec3 p ) {
    vec3 old_x = p * (1.-cos(iTime));
    vec3 nx;
    for (int  i=0; i<5; i++ ) {
        nx = 7.*old_x*(1.-old_x);  //logistic equation
        old_x =  nx;
    }
    return ( nx );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy*4.;

    vec3 rd = normalize( vec3(uv, 2.) );

    vec3 col = cross( lgrnd(rd),
                      vec3(uv, 1.) );


    fragColor = vec4(col,1.0);
}
