float noise(vec3 x) {

    x += vec3(sin(iTime * 0.4),cos(iTime * 0.4),0.0);

    vec3 p = floor(x);
    vec3 f = fract(x );
    f = f*f*(3.-2.*f);

    float n = p.x + p.y*157. + 113.*p.z;

    vec4 v1 = fract(753.5453123*sin(n + vec4(0., 1., 157., 158.)));
    vec4 v2 = fract(753.5453123*sin(n + vec4(113., 114., 270., 271.)));
    vec4 v3 = mix(v1, v2, f.z);
    vec2 v4 = mix(v3.xy, v3.zw, f.y);
    return mix(v4.x, v4.y, f.x );
}


float HN(vec3 p) {

  p = mat3(0.28862355854826727, 0.6997227302779844, 0.6535170557707412,
           0.06997493955670424, 0.6653237235314099, -0.7432683571499161,
           -0.9548821651308448, 0.26025457467376617, 0.14306504491456504)*p;
  return dot(vec4(noise(p), noise(p*sin(iTime * 0.001)), noise(p*sin(iTime * 0.001)*2.0), noise(p*sin(iTime * 0.0001)*4.0)),
             vec4(0.5, 0.25, 0.125, 0.06));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;

    uv += vec2(0.0, iTime * 0.04
 );

    vec3 p = vec3(uv*16., 0.);
    float result = noise(p);

    uv += (HN(p)*0.2);

    ivec2 cell = ivec2(uv * 15.0);
    float dist = 1.0;


    for (int x = 0; x < 4; x++) { 
        for (int y = 0; y < 4; y++) {

        vec2 cell_base = vec2(cell + ivec2(x-2, y-2)) / 15.0;
	    float NX = fract(sin(dot(vec2((cell + ivec2(x-2, y-2))).xy ,vec2(12.9898,78.233))) * 43758.5453);  
        float NY = fract(sin(dot(vec2((cell + ivec2(x-2, y-2)).yx).xy ,vec2(12.9898,78.233))) * 43758.5453);
        //cheaper overlap
        vec2 cell_point = cell_base + (0.5 + 1.5 * vec2(NX, NY)) / 15.0;
        dist = min(dist, distance(cell_point, uv));

        }
    }


    fragColor = vec4(

    1.0 - smoothstep(dist /= length(vec2(1.0 / 15.0)),0.0,0.4)


    );


}
