const vec3 BROWN = vec3(1.0, 0.6, 0.1)*0.4;
const vec3 PINK = vec3(1.0, 0.0, 1.0)*1.5;
const vec3 LIGHTBLUE = vec3(0.0, 0.5, 1.0)*1.5;
const vec3 LIGHTGREEN = vec3(0.0, 1.0, 0.5)*1.5;

const float PI = 3.14156;
const float PI2 = PI * 2.0;

const vec2 CENTER = vec2(0.0, 0.0);

// https://iquilezles.org/articles/distfunctions2d
float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 circle(const float value) {
    return vec2(sin(value), cos(value));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect = vec2(iResolution.x/iResolution.y, 1.0);

    float subseconds = fract(iDate.w);
    float seconds = float((int(iDate.w)) % 60);
    float minutes = float((int(iDate.w/60.0)) % 60);
    float hours = float((int(iDate.w/(60.0*60.0))) % 12);

    vec2 second = circle(PI2*seconds/60.0);
    vec2 minute = circle(PI2*minutes/60.0);
    vec2 hour = circle(PI2*hours/12.0);

    vec2 norm = (uv*2.0-1.0)*aspect;
    vec3 col = vec3(norm, 0);
    col = mix(col, BROWN, smoothstep(1.0, 0.9, length(norm)));

    float secondHandle = 1.0 - clamp((sdSegment(norm*1.25, CENTER, second))*63.0, 0.0, 1.0);
    col = mix(col, PINK, secondHandle);

    float minuteHandle = 1.0 - clamp((sdSegment(norm*1.5, CENTER, minute))*31.0, 0.0, 1.0);
    col = mix(col, LIGHTBLUE, minuteHandle);

    float hourHandle = 1.0 - clamp((sdSegment(norm*2.0, CENTER, hour))*15.0, 0.0, 1.0);
    col = mix(col, LIGHTGREEN, hourHandle);    

    fragColor = vec4(col, 1.0);
}
