vec2 sizpos(vec2 uv, float multiplier){

    uv *= multiplier;
    uv -=multiplier/2.0-0.5;
    uv.x -= 0.5*sin(iTime);
    uv.y -= 0.5*cos(iTime);
    return uv;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float multiplier = 4.0;
    vec2 uv_mod = sizpos(uv, multiplier);

    vec4 particle = texture(iChannel1, uv_mod);
    float mask = step(0.0, uv_mod.x)*step(0.0, uv_mod.y)*((1.0-step(1.0, uv_mod.x))*(1.0-step(1.0, uv_mod.y)))*particle.a;



    // Output to screen
    fragColor = mix(vec4(texture(iChannel0, uv)), particle, mask);
}
