#define PI 3.1415922653

float circle(in vec2 _uv, in float _radius){
    vec2 l = _uv-vec2(0.5);
    return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(l,l)*4.0);
}

vec2 tile(vec2 _uv,float _zoom){
    _uv *= _zoom;
    return fract(_uv);
}

vec2 rotate2D(vec2 _uv, float _angle){
    _uv -= 0.5;
    _uv =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _uv;
    _uv += 0.5;
    return _uv;
}
float box(vec2 _uv, vec2 _size, float _smoothEdges){
    _size = vec2(0.5)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_uv);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_uv);
    return uv.x*uv.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;
    uv.x*=iResolution.x/iResolution.y;
    vec3 color = vec3(.0);


    uv = tile(uv,5.);

    //uv = rotate2D(uv,sin(iTime)*PI);


    uv = rotate2D(uv,PI*0.25);

    //color = vec3(uv,.0);
    //color += vec3(circle(uv,.5));

    color = vec3(box(uv,vec2(0.7),0.01));
    fragColor = vec4(color,1.);
}
