void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float col = uv.x;
    if (uv.y > 0.51)
    {
        col = pow(col, 2.2);
    }
    else if (uv.y < 0.49)
    {
        if (uv.x < 0.04045)
        {
            col = col / 12.92;
        }
        else
        {
            col = pow((col + 0.055) / 1.055, 2.4);
        }
    }

    else
    {
        col = 0.0;
    }


    fragColor = vec4(vec3(col, col, col),1.0);
}
