void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float progress = iMouse.x/iResolution.x;

    float circleMultiplier = 2.;
    float c = 1. - length(uv - 0.5);
    c = smoothstep(1., 1.- progress * circleMultiplier, c);

    vec3 texMain = texture(iChannel1, uv).rgb;
    vec3 texBurned = vec3(texture(iChannel0, uv).r) * (1. - progress);

    vec3 col = mix(texBurned, vec3(1.), c);
    float cut = 0.6;
    col = smoothstep(cut, cut + 0.05, col);
    col = mix(vec3(sin(col.r * 3.), 0., 0.), texMain, col);
    fragColor = vec4(col,1.0);
}
