#define byte 215
vec2 rotate(vec2 xy, float a) {
  return vec2(xy.x*cos(a)-xy.y*sin(a),xy.x*sin(a)+xy.y*cos(a));
}

void mainImage( out vec4 rgba, in vec2 oo )
{
    vec2 o=oo;float t=iTime/10.0;

    int A  =int(mod(float(t*3.0),3.0));
    int B  =int(mod(float(t*4.0),3.0));
    int C  =int(mod(float(t*5.0),3.0));
    int DEF=int(mod(float(t*7.0),3.0));
    int _stars=A,_lines=B,_squares=C,_campaign=DEF;

    o.y-=iResolution.y/2.0;
    if(_stars==0){
    if(o.x<=iResolution.x/3.0)o.x+=iResolution.x/3.0/2.0;
    }
    if(_stars==1)o.x-=iResolution.x/2.0;
    if(_stars==2){
        if(o.x<iResolution.x/2.0){o.x-=iResolution.x/4.0;}
        else{o.x=iResolution.x*3.0/4.0-o.x;}
    }
    float zoom=5.0;o*=zoom;
    o.y=-o.y-72.0;
    vec2 safe=o;
    if(_campaign==0)rgba=vec4(0,0.75,1,0)/2.0;
    else if(_campaign==2)rgba=vec4(0,1,0.5,0)/3.0;
    else if(_campaign==1)rgba=vec4(1,1,1,0)/5.5;
    float[] z=float[](0.,0.,0.,0.,0.);
    for(int i=0;i<5;i++) {
        o=rotate(safe,radians(float(72*i)));
        z[i]=o.y+float(float(byte));
    }
    float r=length(safe);
    float foo=z[0]+z[1]+z[2]+z[3]+z[4];
    float bar=z[0]*z[1]*z[2]*z[3]*z[4];
    if(_stars!=0&&(bar<0.0&&r<float(byte)*3.2||r<float(byte)*1.25)){
        rgba=vec4(1,0,0,0);
        if(foo/zoom!=float(byte))rgba=vec4(1);
    } else if(foo/zoom!=float(byte)){rgba=vec4(foo/zoom<float(byte)-1.0);
        if((safe.y<50.0&&safe.y>.0)&&(_lines==2))rgba=1.0-rgba;
        if((safe.y>-100.0&&safe.y<-50.0)&&_lines==2)rgba=1.0-rgba;
        if((safe.y>-100.0&&safe.y<50.0)&&_lines==1)rgba=1.0-rgba;
        if(_squares>0){
            if(_stars==0){
                oo-=iResolution.xy/2.0;oo.y+=80.0;
                if(_squares==1&&abs(oo.x)<20.0&&oo.y>120.&&oo.y<160.0)rgba=1.0-rgba;
                if(_squares>=2&&oo.x>40.0&&abs(oo.x)<80.0&&oo.y>120.&&oo.y<160.0)rgba=1.0-rgba;
                if(_squares>=2&&-oo.x>40.0&&abs(oo.x)<80.0&&oo.y>120.&&oo.y<160.0)rgba=1.0-rgba;
            }
            vec2 s=safe;
            if(_squares==1)s.x-=310.0;
            if(_squares==2)s.x=abs(abs(safe.x)-310.0);
            s.y+=400.0;
            s*=2.0;
            if(min(max(abs(s.x),abs(s.y)),max(abs(s.x-s.y),abs(s.y+s.x)))<200.0)rgba=1.0-rgba;
        }}
}
