void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;

    if((uv.x-0.5)*(uv.x-0.5)+(uv.y-0.5)*(uv.y-0.5)<0.05){
        col = vec3(1.0,0.4,0.4);
    }else if(uv.y < cos(iTime)*0.25+0.4){
        col = vec3(0.0,0.5,0.7);
    }else if(uv.y < sin(iTime*2.5)*0.25+0.5){
        col = vec3(1.0,1.0,1.0);
    }else {
        col = vec3(0.4,0.8,1.0);
    }

    // Output to screen
    fragColor = vec4(col,0.4);
}
