vec3 hsv(float h, float s, float v) {
  return ((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 r=iResolution.xy, p=(gl_FragCoord.xy*2.-r)/min(r.x,r.y);
  p *= pow(0.5,mod(iTime*4.0,20.0));
  p -= vec2(0.7473,0.1003);
  int j=0;
  vec2 v=vec2(0.0,0.0);
  for(int i=0;i<=1000;++i){
    if (length(v)>2.) break;
    v = vec2(v.x*v.x-v.y*v.y,2.*v.x*v.y)+p;
    j++;
  }
  float c = 1.-float(j)/1000.;
  fragColor=vec4(hsv(c*0.7,.9,1.),1);
}
