//inspired by https://twitter.com/MAKIO135
//random generator idea from https://www.shadertoy.com/view/NlKXDy

void mainImage(out vec4 o, vec2 u)
{
    o = vec4(0, 0, .1, 1);
    for(float r, h = 1e2, l = length(u = u / iResolution.y - .5), t = iTime, i = .1; 
        i < 1.;
        r = fract(h * sin(h * ceil(t + h * atan(u.x, u.y) * i) + i)),
        o += vec4(6e-3 > abs(l - i) && i < r) * 
            cos(vec4(i - h, l * r - h, l * r, 0) * 16. - t),
        i+=.02
    );
}
