// TODO:Â fix aspect ratio: https://www.shadertoy.com/view/XlG3DW

float lum(vec4 c) {
    return 0.21 * c.r + 0.71 * c.g + 0.07 * c.b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 Sres = iResolution.xy;
    vec2 Tres = iChannelResolution[1].xy;
    vec2 ratio = Sres/Tres;

    float scale = 1.2;
    vec2 offset = vec2(0.4);
    vec2 m = iMouse.xy / iResolution.xy;

    vec4 image = texture(iChannel1, (uv / ratio + offset) * scale);
    vec4 noise = texture(iChannel0, (uv * m.x) * scale);


    vec4 col = vec4(0.0);

    if(lum(image) > lum(noise)) {
        col = vec4(1.0);
    }

    fragColor = col;
}
