vec4 tx(vec4 p) {
    return texture(iChannel0,p.yx)*3.;
}

vec4 T(vec4 pp, float a, float t) {
    vec2 w = pp.xz*0.75*a-t*a;
    vec2  r = iResolution.xy, p = w - r*.5;
    float d = length(p) / r.y, c=1., x = pow(d, .1), y = atan(p.x, p.y) / 16.28;

	for (float i = 0.; i < 3.; ++i)    
		c = min(c, length(fract(vec2(x - iTime*i*.15, fract(y + i*.125)*.5)*725.)*7.-1.));

	return vec4(d+.8*c*d*d*(.56-d))/1.;
}

void mainImage( out vec4 o, in vec2 c ) {
    vec4 p = vec4(c,0.,1.)/iResolution.xyxy-.5, d=p, e, z;
    float t = iTime+6., x, r;
    d.y -= .05;
    p.z += t*0.1;
    for(float i=1.; i>0.; i-=.02)
    {
        e = sin(p*10.+t);
        r = pow(length(tx((e+p)))/5.,4.);
        x = abs(p.y+e.x*e.z*(.125+r)-.975);
        e = T(p,.05,t)*T(p,.07,t);
        x -= e.x*.38;
        o = 0.35/length(p.xy) + e*i*i*i;
        if(x<.01) break;
        p -= d*x*.5;
     }
     z = p-vec4(15.,vec3(0.));
     o *= tx(z);
}
