vec2 random(vec2 uv){
    uv = vec2( dot(uv, vec2(127.1,311.7) ),
               dot(uv, vec2(269.5,183.3) ) );
    return -1.0 + 2.0 * fract(sin(uv) * 43758.5453123);
}

float noise(vec2 uv, float seed_h, float seed_v) {

    uv.x = uv.x + seed_h;
    uv.y = uv.y + seed_v;

    vec2 uv_index = floor(uv);
    vec2 uv_fract = fract(uv);

    vec2 blur = smoothstep(0.0, 1.0, uv_fract);

    return mix( mix( dot( random(uv_index + vec2(0.0,0.0) ), uv_fract - vec2(0.0,0.0) ),
                     dot( random(uv_index + vec2(1.0,0.0) ), uv_fract - vec2(1.0,0.0) ), blur.x),
                mix( dot( random(uv_index + vec2(0.0,1.0) ), uv_fract - vec2(0.0,1.0) ),
                     dot( random(uv_index + vec2(1.0,1.0) ), uv_fract - vec2(1.0,1.0) ), blur.x), blur.y) + 0.5;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float resolution = 10.0;
    float horizontal_seed = 0.0;
    float vertical_seed = 0.0;
    float horizontal_speed = 0.0;
    float vertical_speed = 0.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = resolution * fragCoord / iResolution.x;

    // noise
    uv.x = uv.x - iTime * horizontal_speed;
    uv.y = uv.y + iTime * vertical_speed;
    float noise = noise(uv, horizontal_seed, vertical_seed);
    vec3 col = vec3(noise, noise, noise);

    // Output to screen
    fragColor = vec4(col,1.0);
}
