 float Alpha_Percent = 100.0;
 float Offset_Amount = 0.8;
 int xSize = 8;
 int ySize = 8;
 int Reflection_Offset = 2;
 bool Horizontal_Border;
 float Border_Offset = 0.;
 vec4 Border_Color = vec4(.8,.5,1.0,1.0);
 vec4 Glass_Color;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

int xSubPixel = int(uv.x * iResolution.x) % clamp(xSize,1,100);
	int ySubPixel = int(uv.y * iResolution.y) % clamp(ySize,1,100);
	vec2 offsets = vec2(Offset_Amount * float(xSubPixel) / iResolution.x, Offset_Amount * float(ySubPixel) / iResolution.y);
	uv = uv + offsets;
	vec2 uv2 = vec2(uv.x + (float(Reflection_Offset) / iResolution.x),uv.y + (float(Reflection_Offset) / iResolution.y));

	vec4 rgba = texture(iChannel0, uv);
	vec4 rgba_output = vec4(rgba.rgb * Border_Color.rgb, rgba.a);
	rgba = texture(iChannel0, uv);
	vec4 rgba_glass =texture(iChannel0, uv2);

	float uv_compare = uv.x;
	if (Horizontal_Border)
		uv_compare = uv.y;

	if (uv_compare < (Border_Offset - 0.005))
	{
			rgba_output = (rgba + rgba_glass) *.5 * Glass_Color;
	}
	else if (uv_compare >= (Border_Offset + 0.005))
	{
		rgba_output = texture(iChannel0, uv);
	}
	fragColor= mix(rgba,rgba_output,(Alpha_Percent * 0.01));

    }
