void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    vec2 offset1 = vec2(0.01, 0);
    vec2 offset2 = vec2(0.03, 0);
    vec2 offset3 = vec2(0.04, 0);
    if (abs(sin(uv.y - iTime)) < 0.05)
    {
        warpedUv += vec2(sin(uv.y * 2.0 - 1.0)* 0.01, tan(uv.y * 250.0) * 0.11);
    }
    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;




    vec3 col = vec3(r, g, b);


    // Output to screen
    fragColor = vec4(col,1.0);
}
