void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec3 txt = texture(iChannel0, uv).rgb;

    fragColor = vec4(txt , 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    vec2 UV = fragCoord.xy/iResolution.xy;
    float t = iTime * 10.0;
    uv.x += sin(t) * 0.4;
    uv.y += cos(t) * 0.4;
    uv += sin(t * 0.8) * 0.2;
    vec3 col = vec3(0);

    float f = 0.001 / length(uv);

    col += f;

    vec3 txt = texture(iChannel0, UV).rgb;

    col += txt * 0.999999999999;

    //col = col / (1.0 + col);

    fragColor = vec4( col * 1.1 * vec3(0.8,0.9,0.0) , 1.0);
}
