#define i 1.8
#define j 1.7
#define n 0.06*(sin(2.*round(2.*iTime)))

float randomNoise(float x, float y)
{
    return fract(sin(dot(vec2(x, y), vec2(12.9898, 78.233))) * 43758.5453);
}

float _Indensity = 0.02; 


float lum(vec4 c) 
{
        return dot(c.rgb, vec3(0.3, 0.59, 0.11));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 p0 = fragCoord.xy / iResolution.xy;

        vec2 p = vec2(p0.x+n,p0.y);
        vec2 imageIncrement = vec2(1.0, 1.0) / iResolution.xy;

        float t00 = lum(texture(iChannel0, p + imageIncrement * vec2(-i, -i)));
        float t10 = lum(texture(iChannel0, p + imageIncrement * vec2( 0.0, -i)));
        float t20 = lum(texture(iChannel0, p + imageIncrement * vec2( j, -i)));
        float t01 = lum(texture(iChannel0, p + imageIncrement * vec2(-i,  0.0)));
        float t21 = lum(texture(iChannel0, p + imageIncrement * vec2( j,  0.0)));
        float t02 = lum(texture(iChannel0, p + imageIncrement * vec2(-i,  j)));
        float t12 = lum(texture(iChannel0, p + imageIncrement * vec2( 0.0,  j)));
        float t22 = lum(texture(iChannel0, p + imageIncrement * vec2( j,  j)));

        vec2 grad;
        grad.x = t00 + 2.0 * t01 + t02 - t20 - 2.0 * t21 - t22;
        grad.y = t00 + 2.0 * t10 + t20 - t02 - 2.0 * t12 - t22;

        float len = length(grad);

        float splitAmount = _Indensity * randomNoise(iTime, 2.0);

        vec4 ColorR = texture(iChannel0, vec2(p0.x + splitAmount, p0.y));
        vec4 ColorG = texture(iChannel0, p0);
        vec4 ColorB = texture(iChannel0, vec2(p0.x - splitAmount, p0.y));



        if(abs(sin(3.*iTime))<0.5 ){
        fragColor =vec4(ColorR.r, ColorG.g, ColorB.b,1.0);
        }else{

        fragColor = 1.3*vec4( len*len*sin(6.*iTime), len*len*sin(2.*iTime), len*len*sin(8.*iTime), 1.0) +vec4(ColorR.r, ColorG.g, ColorB.b,1.0);
 }
}
