#define FIX 0.001

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 bgTex = texture(iChannel0, uv).rgb;
    vec3 foreTex = texture(iChannel1, uv).rgb;
    vec3 edgeCol = vec3(.7, 0.1, .2);

    float eInB = sin(iTime) * 0.5 + .5;
    float e = eInB * 0.2 + FIX;

    vec4 alphaTex = texture(iChannel2, uv * .3 + 0.2);
    float fB = smoothstep(eInB + e, eInB, alphaTex.r);

    bgTex += edgeCol * fB * 10.;
    vec3 col = mix(bgTex, foreTex, step(1. - fB, 0.));

    fragColor = vec4(col,1.0);
}
