// iq provides a good explanation to clear up confusion in the comments

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // gradient across x 
    vec3 col = vec3(1.0) * uv.x;

    if(uv.y >= .5) 
    {
        // top half is gamma corrected before being sent to screen
        col = pow( col, vec3(1.0/2.2) );

        // Sanity check that this is top half
        // col = vec3(0.5,1.0,.5);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
