const int AMOUNT = 12;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 20.0 * (fragCoord.xy-iResolution.xy/ 2.0 )/ min(iResolution.x, iResolution.y);
    float len;

    for(int i = 0; i < AMOUNT; i++) {
        len = length(vec2(uv.x, uv.y));

        uv.x = uv.x - cos(uv.y +sin(len)) + cos(iTime / 9.0);
        uv.y = uv.y - sin(uv.x +cos(len)) + sin(iTime / 12.0);
    }

    vec3 color = vec3(
        cos(len*0.1),
        cos(len*0.2),
        cos(len*0.5)
        );

    fragColor = vec4(color,1.0);
}
