void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord.xy/iResolution.xy;
    float x = st.x, y = st.y;
    float t = iTime + 400.0;
    float color = 0.0;
    x -= 0.5;
    y -= 0.5;
    x += 0.10 * sin(y * 8.0 + t * 2.5);
    float width = 0.2;
    float angle = t * 2.0 + y*4.0 * sin(-0.020 * t * sin(y*4.0));
    angle = sin(angle * 1.5 * sin(t*0.0007));
    for (int i=0; i<4; i++) {
        float angle2 = 3.14159 / 2.0 * float(i);
        float x0 = cos(angle + angle2) * width;
        float x1 = cos(angle+3.14159/2.0 + angle2) * width;
        float brg = 0.5+0.5*cos(angle+3.14159/4.0 + angle2);
        if (x0 < x && x < x1) {
            color += mix(0.3, 1.0, brg);
            color += pow(clamp(0.5+0.5*cos(angle+3.14159/4.0 + angle2),0.0,1.0), 7.0) * 8.0;
        }
    }
    fragColor = vec4(0.1*color, 0.5*color, color, 1.0);
}
