#ifdef GL_ES
precision highp float;
#endif


vec2 scaleImage(vec2 fragCoordN, float scale){
    return (-scale /2.0) + 
    scale * fragCoordN;
}
vec2 normalizeResolution(vec2 fragCoord) {
    return fragCoord.xy / iResolution.xy;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  vec2 fragCoordN = normalizeResolution(fragCoord);

  float v = 5.0 + 0.25*(iTime);

  vec2 cPos = scaleImage(fragCoordN, v);
  float cLength = length(cPos);

  vec2 uv;
  uv = fragCoordN;
  uv += cPos / cLength;
  uv *= cos(cLength * 15.0 - iTime * 4.0);  
  uv *= 0.73;


  vec3 col = vec3(uv, 1.0);

  fragColor = vec4(col, 1.0);

}
