float customSin(float x) {
    return 0.3 * sin(15.0 * x - iTime * 4.0) + 0.5;
}

vec4 drawSinWave(vec4 sinColor, vec2 uv, float thickness, vec4 BG_COLOR) {
    if (abs(customSin(uv.x) - uv.y) < thickness) {
        return ((0.5, 0.5, 0.5, 1.0) - BG_COLOR);
    } else {
        return BG_COLOR;
    }
}

vec4 drawSinCircle(vec4 circleColor, vec4 originalColor, vec2 uv, float x, float r, vec4 BG_COLOR) {
    vec2 center = vec2(x, customSin(x));
    vec2 stretch = vec2(iResolution.x / iResolution.y, 1);
    float radius = length(stretch * (center - uv));

    if (radius < r) {
        return vec4(0.2, 0.2, 0.2, 0) + radius*15.0*(vec4(1, 1, 1, 1) - BG_COLOR);;
    } else {
        return originalColor;
    }
}

float derivativeCustomSin(float x) {
    return 0.3 * cos(15.0 * x - iTime * 4.0) * 15.0;
}

vec4 drawSinTangent(vec4 originalColor, vec2 uv, float x, float thickness, vec4 BG_COLOR) {
    vec2 center = vec2(x, customSin(x));
    vec2 stretch = vec2(iResolution.x / iResolution.y, 1);
    float m = derivativeCustomSin(x);
    float q = customSin(x) - m * x;

    if (abs(m * uv.x + q - uv.y) < thickness) {
        return vec4(0, 2.0*length(stretch * (center - uv)), 2.0-length(stretch * (center - uv)), 0);;
    } else {
        return originalColor;
    }
}

vec2 rotateVector(vec2 inputVec, float k, vec2 offset) {
    float c = cos(iTime);
    float s = sin(iTime);

    return k * mat2(c, s, -s, c) * (inputVec - offset);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    fragCoord = rotateVector(fragCoord, 2.5, iResolution.xy * 0.5);

    vec2 uv = fragCoord / iResolution.xy;

    vec4 tgcolor = vec4(0, 0.8*length(uv.x), 1, 0.5);

    vec4 bgcolor = 0.5+0.5*tan(0.5*iTime+uv.xyxy+vec4(0,3,-5,1));

    fragColor = drawSinWave(vec4(1, 0, 0, 0), uv, 0.01, bgcolor);

    fragColor = drawSinTangent(fragColor, uv, 0.5, 0.01, bgcolor);

    fragColor = drawSinCircle(vec4(0.64, 0, 1, 0), fragColor, uv, 0.5, 0.05, bgcolor);
}
