#define PI 3.1415926535897932384626433


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     {
       float g = 0.2;
       fragColor = vec4( g, g, g, 1. );
     }


     float x = fragCoord.x / iResolution.x;
     float y = fragCoord.y / iResolution.y;


     float speed = 1.;
     float ax = x - ( cos( mod( iTime*speed, 2. * PI )) / 2. );
     float ay = y - ( sin( mod( iTime*speed, 2. * PI )) / 2. );

     float e = ax * ax + ay * ay;
     e = 1. - e;
     e = e * e * e * e;

     fragColor += vec4( e, 0., e, 1. );
}
