highp float hash(
	highp vec2 x){
return fract(sin(dot(
	x,vec2(1,57)))*4e5);
	}

#define saturate(x) clamp(x,0.0,1.0)
#define chroma_key(x) x.g > x.b+x.r

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec2 uv = fragCoord/iResolution.xy;

vec3 col = texture (iChannel0, uv).rgb;
vec2 lightpos = vec2(0.5,0.5);
uv -= lightpos;
vec4 ray = texture(iChannel0,(uv*hash(
floor(uv*iResolution.xy))+lightpos));

ray.a = float(chroma_key(ray));
     /*you can replace 
     this with luma*/

if(chroma_key(col))
     col = vec3(0);
     //background

col = mix(col, vec3(1)/*rayColor*/,
      ray.a*saturate(1.0-length(uv)));

//float sound = texelFetch(iChannel1, ivec2(1), 0).a;
//col *= sound;

    fragColor = vec4(col,1.0);
}
