float inHeart (vec2 uv, vec2 center, float size) {
  if (size==0.0) return 0.0;
  vec2 o = vec2((uv.x-center.x)/(1.2*size),(uv.y-center.y)/(1.6*size));
  float a = o.x*o.x+o.y*o.y-0.3;
  return step(a*a*a, o.x*o.x*o.y*o.y*o.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 heart = vec4(vec3(inHeart(uv, vec2(0.5,abs(sin(iTime))), 0.15)), 1.0);




    // Output to screen
    fragColor = vec4(texture(iChannel0, uv)+heart);

}
