void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 centered = fragCoord - iResolution.xy / 2.0;
    //vec2 scaled = centered / 60.0;
    vec2 scaled = vec2(centered.x / 60.0, centered.y / 15.0);

    float dist = length(scaled);
    float whole = round(dist);
    float distToRing = abs(dist - whole);

    vec3 col = vec3(distToRing < 0.04 ? 1.0 : 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
