void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (texelFetch(iChannel0, ivec2(32, 0), 0).x > 0.)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0);
        return;
    }
    if(length(fragCoord.xy - iMouse.xy) > 1.0)
        discard;
    if(iMouse.z > 0.)
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);

}
