void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

      //Remap
    uv = 2.*uv-1.;
    uv.x *= iResolution.x/iResolution.y;
    vec3 color =vec3(0.0);
    float d = 0.;


    //SDF
    d = length(abs(uv)-.3);

    d += length( min(abs(uv)-.3,0.));
    d += length( max(abs(uv)-.7,0.));
    d += length(abs(cos(uv*5.)*sin(uv*3.))*.8+.1);
    //Visualize SDF

    //lor = vec3(uv.x);
    color += vec3(fract(d*10.)+sin(1.5*iTime));
    color += vec3(step(.3,d)+cos(1.5*iTime));
    color += vec3(smoothstep(.3,.4,d)*smoothstep(.6,.5,d));
    fragColor = vec4(color,1.0);



}
