float circle(vec2 uv, vec2 pos, float r, float blur){
    vec2 a = uv-pos;
    return smoothstep(r+blur, r-blur, sqrt(a.x*a.x+a.y*a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 R = iResolution.xy;
    vec2 uv = vec2(fragCoord.x-R.x/2., fragCoord.y-R.y/2.);
    vec3 col = vec3(0.);

    float d = 15.;
    float s = 40.;
    if(iMouse.xy != vec2(0)){
    d = iMouse.x/10.;
    s = iMouse.y/2.;
    }
    vec2 cpos = vec2(round(uv.x/d)*d, round(uv.y/d)*d);
    float cradius = (abs(uv.x)+abs(uv.y))/75.+3.;
    float cblur = abs((abs(uv.x)+abs(uv.y))/50.-sin(iTime*2.)*10.)+1.;
    col += circle(uv, cpos, cradius, cblur)*vec3(abs(round(cpos/s)*s), 0.)/200.;

    fragColor = vec4(col,1.0);
}
