{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}

#define BLURAMOUNT 2.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec3 col = vec3(0.0, 0.0, 0.0);

    vec3 average = vec3(0.0);

    float amount = 0.0;
    for (float y = -BLURAMOUNT; y <= BLURAMOUNT; y += 1.0) {
        for (float x = -BLURAMOUNT; x <= BLURAMOUNT; x += 1.0) {
            vec3 t = texture(iChannel0, uv + vec2(x, y) / iResolution.xy).rgb;
            average += t;
            amount += 1.0;

        }
    }

    col = average / amount * 0.8;

    vec2 target = vec2(sin(iTime) * 0.5 + 0.5, 0.5);

    if (distance(uv, target) < 0.1) {
        col = vec3(6.0, 1.0, 1.2);
    }





    fragColor = vec4(col,1.0);
}
