renderpass.code:
 float Speed_Percent = 100.0f ;
 float Focus_Percent = 15.0f;
 bool Glitch = false;
 vec4 Spotlight_Color = vec4(1,1,1,1);
 float Horizontal_Offset = 0.0f;
 float Vertical_Offset = -0.5f;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   float speed = Speed_Percent * 0.01;
	float focus = Focus_Percent;
	if (Glitch)
	{
		speed *= ((2. * 2.) - 1.) * 0.01;
		focus *= ((2. * 1.1) - 0.1);
	}

	float PI = 3.1415926535897932384626433832795;//acos(-1);
	vec4 c0 = texture(iChannel0, uv);
	vec3 lightsrc = vec3(sin(iTime * speed * PI * 0.667) *.5 + .5 + Horizontal_Offset, cos(iTime * speed * PI) *.5 + .5 + Vertical_Offset, 1);
	vec3 light = normalize(lightsrc - vec3( uv.x + (Horizontal_Offset * speed),  uv.y + (Vertical_Offset * speed), 0));
	c0 *= pow(dot(light, vec3(0, 0, 1.)), focus) * Spotlight_Color;

	fragColor = c0;

}
