void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;   

    // Get random value with time
    float time = 0.5 * cos(iTime) - 1. * iTime;
    // Offset y values from x values
    float o = 0.1;
    float sc = 0.8;// + 0.5 * cos(iTime);

    // Next 4 points in sequence
    // (bad approach - has a bottom-left bias)
    vec2 p0 = -sc * 0.5 + sc * vec2( h21(vec2(floor(time))),      h21(vec2(o + floor(time))) );
    vec2 p1 = -sc * 0.5 + sc * vec2( h21(vec2(floor(time + 1.))), h21(vec2(o + floor(time + 1.))) );
    vec2 p2 = -sc * 0.5 + sc * vec2( h21(vec2(floor(time + 2.))), h21(vec2(o + floor(time + 2.))) );
    vec2 p3 = -sc * 0.5 + sc * vec2( h21(vec2(floor(time + 3.))), h21(vec2(o + floor(time + 3.))) );

    //float l12 = length(p2 - p1);
    //float l23 = length(p3 - p2);

    vec2 d12 = normalize(p2 - p1);
    vec2 d23 = normalize(p3 - p2);

    float k = 1.; // idk what value is good here

    // x0 lies on p2 -> p1 line, x1 lies on p3 -> p2 line
    vec2 x0 = p1 - k * d12;
    vec2 x1 = p2 - k * d23;

    // p0 -> x0 line
    float m0 = (x0.y - p0.y) / (x0.x - p0.x);
    float c0 = p0.y - m0 * p0.x;

    // p1 -> x1 line
    float m1 = (x1.y - p1.y) / (x1.x - p1.x);
    float c1 = p1.y - m1 * p1.x;

    // Intersect x1 -> p1 line with p0 -> x0 line to get A,
    // Lerp (p0 -> A) -> (A -> p1)   
    float Ax = -(c1 - c0) / (m1 - m0);
    float Ay = m1 * Ax + c1;
    vec2 A = vec2(Ax, Ay); 
    // (I think nearly parallel lines mess up A, make it go beyond bounds)
    A = clamp(A, -0.5, 0.5);

    float f = fract(time);
    //f = smoothstep(0., 1., f);
    //f = 16. * f * f * (1.-f) * (1.-f); // <- useful to visualise how it fails
    vec2 q0 = mix(p0, A, f);
    vec2 qA = mix(A, p1, f);
    vec2 q = mix(q0, qA, f);

    // Distance from point + strength to smoothstep away from it
    float d = length(uv-q);
    float k2 = 0.06 + d;

    // Draw stuff
    float s = smoothstep(-k2, k2, -d + 0.);
    float x = texelFetch( iChannel0, ivec2(fragCoord), 0 ).x;
    x = 0.98 * clamp(x, 0., 1.);

    fragColor = vec4(max(x, 2. * s));

}

// Collection of functions I use a lot:

#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Get "point" from Buffer A
    vec2 p = texelFetch( iChannel0, ivec2(fragCoord), 0 ).xy;

    // Distance from point + strength to smoothstep away from it
    float d = length(uv-p);
    d = pow(4. * d * (1.-d), 2.);
    float k = 0.2 + d;

    // Draw stuff
    float s = smoothstep(-k, k, -d + 0.);
    float x = texelFetch( iChannel1, ivec2(fragCoord), 0 ).x;
    x = 0.98 * clamp(x, 0., 1.);

    fragColor = vec4(max(x, 2. * s));
}
