// Author: Chandu JR
// Title: Eye in a Bottle

#ifdef GL_ES
precision mediump float;
#endif

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.02, pct+0.036, st.y);
}

float circle(vec2 st, vec2 or, float rad) {
    float c = 0.0;
    c = smoothstep(rad+0.330, rad-0.05, length(st - or));
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;

    float y = (0.07*(1.0 + sin(st.x + iTime * 2.0)) * 0.5) + -0.030;

    vec3 waterCol = vec3(0.058,0.856,1.000);
    vec3 bgCol = vec3(0.990,0.963,0.504);
    vec3 eyeCol = vec3(0.980,0.614,0.074);

    vec3 color = waterCol;

    float pct = plot(st,y);
    color = (1.0-pct)*color;
    color += pct*bgCol;

    vec3 circ = circle(st, vec2(0.010,0.020), 0.180)*eyeCol;
    color += circ - vec3(0.557,0.600,0.491);

    fragColor = vec4(color,1.0);
}
