// Author: Noel Llorca Jr, Technical Artist, Ubisoft PHL

// Summary: A basic study and visualization of math functions used commonly in game development.
// Im new here in shadertoy, for any mistake, or needs improvement, 
// your constructive feedback is highly appreciated!

// Function taken from my previous work: https://www.shadertoy.com/view/stGfRd
// Parameters: Size1, Size2, UV, function, background, linecolor
vec3 DrawLine(float s1, float s2,vec2 st, float fn, vec3 c1,  vec3 c2)
{
  float line = smoothstep( fn - s1, fn, st.y) - smoothstep( fn, fn + s2, st.y);
  return c1  * (1. - line) + line * c2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Use polar coordinates instead of cartesian
    vec2 toCenter = vec2(0.5)-uv;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*2.0;

    // Colors
    vec3 color = vec3(.23);
    vec3 LineColor = vec3(1.000,0.000,0.635);

    // Time and Speed where time is = amplitude in 0-2 range
    float TimeAmp =  min(1.,sin(iTime / 3.5)); 

    // Full sine period 2 PI!
    float TheFunction = .5 + sin( radius / (0.318 / 2.)) * .5* TimeAmp; 

    // UV movement
    vec2 q = uv;
    q.y += 1. - TheFunction;

    // Texture normal workflow nothing change, uv changed by function
    vec3 Texture = vec3(texture(iChannel0, q).rgb); 

    // Vis Function
    color = DrawLine(.01, .01, uv, TheFunction, Texture, LineColor); 

    // Finally render!     
    fragColor  = vec4(color , 1.0);
}
