void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy / 20.0;
    const float thickness = 0.05;
    const vec4 gridColor = vec4(0.2, 0.2, 0.2, 1.0);
    vec2 middle = iResolution.xy / 2.0;

    vec2 uv = fragCoord/iResolution.xy;

    vec2 fragPosFract = fract(fragCoord/res);

    if(fragPosFract.x < thickness/2.0 || fragPosFract.x > (1.0-thickness/2.0))
        fragColor = gridColor;
    else if(fragPosFract.y < thickness/2.0 || fragPosFract.y > (1.0-thickness/2.0))
        fragColor = gridColor;
    else
        fragColor = vec4(0, 0, 0, 1);

    if(fragCoord.x - middle.x < thickness * res.x && middle.x - thickness < fragCoord.x)
        fragColor.x = 1.0;
    if(fragCoord.y - middle.y < thickness * res.y && middle.y - thickness < fragCoord.y)
        fragColor.z = 1.0;

}
