#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
float map (vec2 u) {
    u+= 1.;
    for (float i = 0.; i < 5.; i++) 
        u=(u-exp(-.9*i))*ei(iTime),
        u = abs(u);
    return abs(length(u)-.05);
}
Main {
    U = 2.*(U-.5*R)/R.y;
    Q = vec4(.1);

    vec2 u = floor(U)+.5;
    float s = 1.;
    for (float i = 0.; i < 10.; i++) {
        vec2 v = abs(U-u)-.5*s;
        Q -= smoothstep(-2./R.y,0.,max(v.y,v.x));
        s *= 0.5;
        if (map(u)/s>3.1)break;
        u = floor(U/s)*s+.5*s;
        Q += .15;
    }
}
