void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 st = uv*10.;

    st.x *= iResolution.x/iResolution.y;

    float pct = 0.0;
    float x = step(mod(st.y, 2.), 1.);
    float y = step(mod(st.x, 2.), 1.);
    if (x == 1.) {
       st.x = clamp(mod(iTime, 2.), 1., 2.) +st.x;  
    } else {
       st.x = clamp(mod(iTime, 2.), 1., 2.) -st.x;
    }

    if (y == 1.) {
       st.y = clamp(mod(iTime, 2.), 0., 1.) +st.y; 
    } else {
       st.y = clamp(mod(iTime, 2.), 0., 1.) -st.y;
    }

	st = fract(st);

    pct = length(st-0.5);
    pct = smoothstep(0.25, 0.3, pct);

    vec3 color = vec3(pct);

    // Output to screen
    fragColor = vec4(color,1.0);
}
