void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = vec4(uv.xy * 1.5,0.9,1.0);

    // Set Offset
    vec2 offset = vec2(4.0/4.5 - cos(iTime) / 5.0, 0.5 + sin(iTime) / 5.0);

    // Draw sphere
    if (pow(uv.x* 8./4.5 - offset.x,2.0) + pow(uv.y - offset.y,2.0) > 0.05) {
    fragColor.rgb = vec3(0,0,0);
    }
}
