#define R 1.
#define RD .5
#define Speed .3

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = abs(RD - mod(iTime * Speed, RD + RD)) + R;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).xyz;

    vec3 bgcol = texture(iChannel1, uv).xyz;

    vec2 mpos = iMouse.xy/iResolution.xy;
    float dist = distance(mpos, uv);

    float alph = min( atanh(dist * r), 1.0); 

    vec3 ocol = col * alph + bgcol * (1.0 - alph);


    // Output to screen
    fragColor = vec4(ocol, 1.0);
}
