// https://youtu.be/YU3DVDMUWTs?t=48
float modulo(float a, float b) {
    return (a)-(floor((a)/(b))*(b));
}
float random (float seed){
    return fract(sin(dot(vec2(seed),vec2(12.9898,78.233)))*43758.5453123);
}
float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 bg = vec3(.8);
    vec3 fg1 = vec3(.2);
    vec3 fg2 = vec3(0.,.9,0.);
    vec3 col = bg;
    float PI = 3.1415;
    float r1 = .17;
    float r2 = .4;
    float a1 = map(fract(iTime / 5.), 0., 1., -PI, PI);
    float a2 = a1+.1;//a1 + sin(fract(iTime * 1.)) / 5.;
    float a2col_transition = smoothstep(0., 1., fract(iTime * 2.));
    vec3 a2col = vec3(1.,a2col_transition,a2col_transition);
    float spin_dir = 1.;
    if (modulo(iTime, 10.) >= 5.){
        spin_dir = -1.;
    }

    if (atan(-uv.x, -uv.y) <= a1 * spin_dir && length(uv) < r2)
        col = fg1;
    else if ((atan(-uv.x, -uv.y) <= a2 * spin_dir && length(uv) < r2))
        col = a2col;
    else
        col = fg2;

    if (length(uv) < r1 || length(uv) >= r2)
        col = bg;

    fragColor = vec4(col,1.0);
}
