// Display üåµ
Main 
{
    vec4 f = A(U), b = B(U);
    Q = 1.-(.5-0.5*(sin(3.5-.3*(f.z)+vec4(1,2,3,4))))*f.w;
    Q = 1.5*(vec4(.9,.7,1,1)-.8*Q)*vec4(1,.5,0,1);
}

// Forces ü¶ú
Main
{
    Q = A(U);
    vec4 bb = B(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        vec4 b = B(U+u);
        float f = 0.08*((b.x-bb.x)+a.w*((a.w-.5)+.3));
        dQ.xy -= f*u;
    }
    Q += dQ;
    Q.xy -= Q.xy*.1*exp(-18.*Q.w*Q.w);

    Q.y -= 1e-3;
    vec2 M = 1.5*R;
    if (iMouse.z>0.) M = iMouse.xy;
    if(length(U-M)<.02*R.y)Q = vec4(.1*normalize(iMouse.xy-0.5*R),-1,1.);
    if (length(Q.xy)>.5) Q.xy = .5*normalize(Q.xy);
    if (iFrame < 1) Q = vec4(0);
    if (U.x < 1.||R.x-U.x<2.) Q.xy *= 0.;
    if (U.y < 1.||R.y-U.y<2.) Q.w *= 0.;
    if (R.y-U.y<6.) Q = vec4(0,-.5,0,.1);
    Q.xy = clamp(Q.xy,-.5,.5);
}

// Advect üêø
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5+.5*max(1.-2.5*Q.w*q.w,0.);
       float k = .5+.5*max(1.-2.5*Q.w*q.w,0.);
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}

// Advect üêø
Main
{
    Q = A(U);
    vec4 dQ = vec4(0);
    box if(abs(x)!=abs(y))
    {
        vec2 u = vec2(x,y);
        vec4 q = A(U+u);
        vec2 a = Q.xy,
             b = q.xy+u;
       float ab = dot(u,b-a);
       float i = dot(u,(0.5*u-a))/ab;
       float j = .5;
       float k = .5;
       float wa = 0.25*Q.w*min(i,j)/j;
       float wb = 0.25*q.w*max(k+i-1.,0.)/k;
        dQ.xyz += Q.xyz*wa+q.xyz*wb;
        dQ.w += wa+wb;

    }
    if (dQ.w>0.)dQ.xyz/=dQ.w;
    Q = dQ;

}

// Land üèî
Main
{
    vec4 a = A(U);
    Q = B(U);
    vec4 
         n = B(U+vec2(0,1)),
         e = B(U+vec2(1,0)),
         s = B(U-vec2(0,1)),
         w = B(U-vec2(1,0)),
         m = 0.25*(n+e+s+w);
    float l = length(a.xy);
    Q = Q+1e-2*(m-Q)- 5e-3*a.w*l+1e-4;
    Q = clamp(Q,0.,1.);
    if (iFrame < 1)
        Q = vec4(1);
}
