void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float speed = 0.25;
    float deviation = 0.15;
    float variation = 2.5;
    float repeats = 4.0;

    vec2 texture_pos = uv * repeats + sin(iTime * speed + uv.yx * variation * repeats) * deviation;
    float colour = pow(texture(iChannel0, texture_pos).r / 1.0, 8.0);

    // Output to screen
    fragColor = vec4(colour, colour, colour, 1.0);
}
