float color(vec2 ch, vec2 a, vec2 b, vec2 c, float bb, float cb){
    return 9.0 / (a.x * a.x + a.y * a.y)  +
    bb * 4.0 / sqrt(b.x * b.x + b.y * b.y)  + 
    cb * 4.0 / sqrt(c.x * c.x + c.y * c.y) ;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 a = fragCoord - iResolution.xy * 0.5;
    float dx = iResolution.x * 0.4;
    float dy = iResolution.x * 0.3 / 16.0 * 9.0;

    vec2 b = a + vec2(dx * cos(iTime), dy * sin(iTime));
    vec2 c = a - vec2(dx * cos(iTime), dy * sin(iTime));

    float cl = color(fragCoord, a, b, c, 8.0 + 7.5 * sin(sin(iTime)), 8.0 - 7.5 * sin(sin(iTime)));
    cl *= iResolution.x / 1000.0;
    fragColor = vec4(cl * (0.5 + 0.5 * cos(iTime)), cl * (0.5 + 0.5 * sin(iTime)), cl * (0.5 - 0.25 * sin(iTime)), 1.0);
}
