#define PI 3.14159265359
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    uv *= 20.;
    //abs
    float f = abs(sin(uv.x));
    //"Capacitors" flatten the signal
    f*=(1.-fract(uv.x/PI));
    f*=(1.-fract(uv.x/PI));

    //Lerp states for graph
    float ff;
    if(uv.x<-PI*2.){
        ff=sin(uv.x);
    }else{
        ff=mix(abs(sin(uv.x)),f,clamp(0.,1.,uv.x*.05));
    }

    float col = smoothstep(ff-.05,ff,uv.y) - smoothstep(ff+.1,ff+.15,uv.y);

    // Output to screen
    fragColor = vec4(col);
}
