#define N_SEGS 32

#define iT iTime
#define iR iResolution.xy
#define CSQR(a) (mat2(a,-(a).y,(a).x)*(a))
#define MIT(c,z) (CSQR(z)+c)

float dLine(vec2 p, vec2 a, vec2 b, float w) 
{
    b -= a; p -= a;
    return smoothstep(w,.0,length (p - clamp (dot (p, b) / dot (b, b), .0, 1.)*b));
}

void mainImage( out vec4 k, in vec2 p )
{
    p -=iR/2.; p /= iR.y*1.8; p+=vec2(-.45,.05);; 
    float t = .05*cos(iT+3.1)-.04;               // float around the initial point

    vec2 c = vec2(-.7,.16)+t;                    // starting point of mandelbrot orbit

    k = vec4(0);
    vec2 z = c, a=CSQR(z)+c, b=z, d; 

    for(int i=N_SEGS; i>0; i--) {

        d = vec2(z); z=MIT(c,z);
        k.b += dLine(p,d,z,.001)*max(0.,4.-iT);  // fade out inner spokes

        k.r += dLine(p,a,z,.01); a = z;          // red spiral
        k += smoothstep(.01,.0,length(p-z));

        d = vec2(z); z=MIT(c,z);
        k.b += dLine(p,d,z,.001)*max(0.,4.-iT);  // fade out inner spokes

        k.g += dLine(p,b,z,.01); b = z;          // green spiral
        k += smoothstep(.01,.0,length(p-z));

    }

    k /= 1.5;
    k = clamp(k,0.,1.);
}
