const vec2 fireMovement=vec2(-0.01, -0.5);
const vec2 distortionMovement=vec2(-0.01, -0.3);
const float normalStrength=40.0;
const float distortionStrength=0.1;
vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

	return -1.0 + 2.0*fract(sin(p) * 43758.5453123);
}
float noise( in vec2 p ) {       // generates simplex noise
    const float K1 = 0.36602540; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y) * K1 );

    vec2 a = p - i + (i.x+i.y) * K2;
    vec2 o = step(a.yx,a.xy);
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}
float fbm ( in vec2 p ) {    //generates fractal noise
    float f = 0.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    f  = 0.5000*noise(p); p = m*p;
    f += 0.2500*noise(p); p = m*p;
    f += 0.1250*noise(p); p = m*p;
    f += 0.0625*noise(p); p = m*p;
    f = 0.5 + 0.5 * f;
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    float timeScale=iTime*0.34;
    vec2 uv = fragCoord.xy/iResolution.xy;

    vec2 uvT = (uv * vec2(1.0, 0.5)) + fireMovement * timeScale;  //creates an illusion of moving the fire upwards
    float n = pow(fbm(8.0 * uvT), 0.8); //noise

    float gradient = pow(1.0 - uv.y, 2.0) * 5.;  //makes a quadratic graient
    float finalNoise = n * gradient;
    vec3 weightedColor=vec3(240.,120.,0.)/255.;

    vec3 finalColor = finalNoise * weightedColor;
    fragColor = vec4(finalColor, 1.0);
}
